                    </div>
                </div>

                <div className="form-container bg-[#2e2750] p-5 rounded-2xl m-4 lg:w-[40%] lg:m-2">
                    <ContactForm/>
                </div>
            </div>
            {/* now i have to create form and add links  */}


        </div>
    );
}

